mapper.select:
SELECT info.agent_merchant_id, info.agent_merchant_name, info.agent_merchant_short_name, info.agent_identity_type, info.agent_identity_num, info.agent_identity_url, info.agent_phone, info.agent_phone_status, info.agent_email, info.agent_email_status, info.agent_qq, info.agent_status FROM agent_merchant_info info
and info.agent_merchant_id=#{agentMerchantId,jdbcType=VARCHAR}
and info.agent_merchant_name=#{agentMerchantName,jdbcType=VARCHAR}
and info.agent_status=#{agentStatus,jdbcType=INTEGER}
