mapper.select:
select

from merchant_register_collect


   and id= #{id,jdbcType=VARCHAR}


   and mer_id=  #{merId,jdbcType=VARCHAR}


  and mer_order_id = #{merOrderId,jdbcType=VARCHAR}


   and terminal_mer_id = #{terminalMerId,jdbcType=VARCHAR}


   and terminal_mer_name =   #{terminalMerName,jdbcType=VARCHAR}


    and card_type= #{cardType,jdbcType=VARCHAR}



    and card_prop= #{cardProp,jdbcType=VARCHAR}


    and bank_code=#{bankCode,jdbcType=VARCHAR}


    and card_num= #{cardNum,jdbcType=VARCHAR}


    and year= #{year,jdbcType=VARCHAR}


    and month= #{month,jdbcType=VARCHAR}



    and cvv=  #{cvv,jdbcType=VARCHAR}


    and  bank_card_phone = #{bankCardPhone,jdbcType=VARCHAR}



    and  settle_card_type = #{settleCardType,jdbcType=VARCHAR}



    and settle_card_prop = #{settleCardProp,jdbcType=VARCHAR}


    and  settle_card_num = #{settleCardNum,jdbcType=VARCHAR}



    and settle_bank_code= #{settleBankCode,jdbcType=VARCHAR}


    and settle_bank_branch_no = #{settleBankBranchNo,jdbcType=VARCHAR}


    and settle_bank_branch_name=#{settleBankBranchName,jdbcType=VARCHAR}


    and expire_date= #{expireDate,jdbcType=VARCHAR}


    and extra_channel_id= #{extraChannelId,jdbcType=VARCHAR}


    and pay_type=#{payType,jdbcType=INTEGER}


    and back_data= #{backData,jdbcType=VARCHAR}


    and result= #{result,jdbcType=VARCHAR}


    and STATUS= #{status,jdbcType=INTEGER}


    and  trade_fee = #{tradeFee,jdbcType=DECIMAL}


    and back_fee=#{backFee,jdbcType=DECIMAL}


    and TIME=#{time,jdbcType=TIMESTAMP}

select

from merchant_register_collect
where mer_id = #{merId} and terminal_mer_id=#{terminalMerId} and STATUS=#{status}

select

from merchant_register_collect
where mer_id = #{merId} and mer_order_id=#{merOrderId}
